import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-quiz-page',
  templateUrl: './quiz-page.component.html',
  styleUrls: ['./quiz-page.component.css']
})
export class QuizPageComponent implements OnInit {
  playerData: any;
  questions: any[] = [];
  currentQuestion: any;

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.playerData = JSON.parse(localStorage.getItem('playerdata') || '{}');
    this.getQuestions();
  }

  getQuestions() {
    const questionsUrl = 'http://localhost:5002/fragen';
    const answersUrl = 'http://localhost:5002/antworten';
    this.http.get<any[]>(questionsUrl).subscribe((questionsResponse: any[]) => {
      this.http.get<any[]>(answersUrl).subscribe((answersResponse: any[]) => {
        const filteredQuestions = questionsResponse.filter(q => q.kategorien_id === this.playerData.kategorien_id);
        filteredQuestions.forEach(question => {
          const questionAnswers = answersResponse.filter(a => a.frage_id === question.id);
          this.questions.push({
            ...question,
            answers: questionAnswers
          });
        });
        this.displayRandomQuestion();
      });
    });
  }

  displayRandomQuestion() {
    const randomIndex = Math.floor(Math.random() * this.questions.length);
    this.currentQuestion = this.questions[randomIndex];
    this.shuffle(this.currentQuestion.answers);
  }

  shuffle(array: any[]) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
  }
}
