import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { HttpClient } from '@angular/common/http';
import { ReactiveFormsModule } from '@angular/forms';

@Component({
  selector: 'app-login-page',
  templateUrl: './login-page.component.html',
  styleUrls: ['./login-page.component.css']
})
export class LoginPageComponent {
  constructor(private http: HttpClient, private router: Router) {}

  user: string = "";
  password: string = "";

  login() {
      if(this.user === 'admin' && this.password === '1234'){
        this.router.navigate(['/', 'dashboard'])
      }
    }
}


<app-header pageTitleRight = "Welcome"></app-header>

<div class="titleLogin">
    <h1>Login</h1>
</div>

<div class ="outerFormDiv">
   <form (ngSubmit) ="login()">
    <div class="mb-3">
      <label for="exampleInputEmail1" class="form-label">Username</label>
      <input type="email" class="form-control" id="inputEmail" name="user" [(ngModel)]="user" aria-describedby="emailHelp">
    </div>
    <div class="mb-3">
      <label for="exampleInputPassword1" class="form-label">Password</label>
      <input type="password" class="form-control" id="inpuPassword" name="password" [(ngModel)]="password" required>
    </div>
    <button type="submit" class="btn btn-primary">Login</button>
  </form> 
</div>
