import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-quiz-page',
  templateUrl: './quiz-page.component.html',
  styleUrls: ['./quiz-page.component.css']
})
export class QuizPageComponent implements OnInit {
  playerData: any;
  questions: any[] = [];
  currentQuestion: any = null;
  currentAnswers: any[] = [];


  constructor(private http: HttpClient) { }

  ngOnInit() {
    const storedPlayerData = localStorage.getItem('playerdata');

    if (storedPlayerData) {
      this.playerData = JSON.parse(storedPlayerData);
    }
    //this.playerData = JSON.parse(localStorage.getItem('playerdata') || '{}');
    console.log(this.playerData);
    this.getQuestions();
  }

  getQuestions() {
    const questionsUrl = 'http://localhost:5002/fragen';
    const answersUrl = 'http://localhost:5002/antworten';
    this.http.get<any[]>(questionsUrl).subscribe((questionsResponse: any[]) => {
      console.log('questionsResponse:', questionsResponse); // Add console log here
      this.http.get<any[]>(answersUrl).subscribe((answersResponse: any[]) => {
        const filteredQuestions = questionsResponse.filter(q => q.kategorien_id === parseInt(this.playerData.kategorien_id));
        console.log('filteredQuestions:', filteredQuestions); // Add console log here
        filteredQuestions.forEach(question => {
          const questionAnswers = answersResponse.filter(a => a.frage_id === question.id);
          this.questions.push({
            ...question,
            answers: questionAnswers
          });
        });
        console.log('questions:', this.questions); // Add console log here
        this.displayRandomQuestion();
      });
    });
  }


  displayRandomQuestion() {
    const randomIndex = Math.floor(Math.random() * this.questions.length);
    this.currentQuestion = this.questions[randomIndex];
    this.currentAnswers = this.currentQuestion.answers;
    console.log('currentQuestion:', this.currentQuestion); // Add console log here
    console.log('currentAnswers:', this.currentAnswers); // Add console log here
  }



  shuffle(array: any[]) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
  }

  answerQuestion(selectedAnswer: any) {
    console.log('User selected answer:', selectedAnswer);
  }

}
