import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-question-page',
  templateUrl: './question-page.component.html',
  styleUrls: ['./question-page.component.css']
})
export class QuestionPageComponent implements OnInit {
  questions: Array<any> = [];
  categories: Array<any> = [];
  answers: Array<any> = [];

  constructor(private http: HttpClient) { }

  private getAnswerById(answerId: number, answers: Array<any>): string {
    const answer = answers.find(a => a.id === answerId);
    return answer ? answer.antwort : '';
  }

  ngOnInit() {
    const categoriesUrl = 'http://localhost:5002/kategorien';
    const questionsUrl = 'http://localhost:5002/fragen';
    const answersUrl = 'http://localhost:5002/antworten';

    this.http.get<Array<any>>(categoriesUrl).subscribe((categoriesResponse) => {
      this.categories = categoriesResponse;

      this.http.get<Array<any>>(questionsUrl).subscribe((questionsResponse) => {
        this.http.get<Array<any>>(answersUrl).subscribe((answersResponse) => {
          this.answers = answersResponse;

          this.questions = questionsResponse.map(question => {
            const category = this.categories.find(cat => cat.id === question.kategorien_id);
            const answers = [
              this.getAnswerById(question.falscheAntwort1, this.answers),
              this.getAnswerById(question.falscheAntwort2, this.answers),
              this.getAnswerById(question.falscheAntwort3, this.answers),
              this.getAnswerById(question.richtigeAntwort, this.answers),
            ];
            return { ...question, categoryName: category?.name, answers };
          });
        });
      });
    });
  }




}
