<div class="container">
    <div class="question-container">
        <h3 class="text-center">Questions</h3>
        <table class="table table-bordered">
            <thead>
                <tr>
                    <th>Question</th>
                    <th>Wrong Answer 1</th>
                    <th>Wrong Answer 2</th>
                    <th>Wrong Answer 3</th>
                    <th>Correct Answer</th>
                    <th>Category</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                <tr *ngFor="let question of questions; let i = index">
                    <td *ngIf="!question.editing">{{ question.question }}</td>
                    <td *ngIf="!question.editing">{{ question.wrongAnswer1 }}</td>
                    <td *ngIf="!question.editing">{{ question.wrongAnswer2 }}</td>
                    <td *ngIf="!question.editing">{{ question.wrongAnswer3 }}</td>
