                    <td *ngIf="!question.editing">{{ question.category }}</td>
                    <td>
                        <button *ngIf="!newRow.editing" type="button" class="btn btn-success action-btn" (click)="editQuestion(question)"><i class="fas fa-edit"></i></button>
