                </tr>
                <tr *ngIf="newRow.editing">
                    <td><input [(ngModel)]="newRow.question" placeholder="Question" class="new-input"></td>
                    <td><input [(ngModel)]="newRow.falscheAntwort1" placeholder="Wrong Answer 1" class="new-input"></td>
                    <td><input [(ngModel)]="newRow.falscheAntwort2" placeholder="Wrong Answer 2" class="new-input"></td>
                    <td><input [(ngModel)]="newRow.falscheAntwort3" placeholder="Wrong Answer 3" class="new-input"></td>
                    <td><input [(ngModel)]="newRow.richtigeAntwort" placeholder="Correct Answer" class="new-input"></td>
                    <td>
                        <select type="text" [(ngModel)]="newRow.kategorien_id">
                            <option *ngFor="let category of categories" [value]="category.id">{{ category.name }}</option>
                        </select>
                    </td>
                    <td>
                        <button *ngIf="newRow.editing" type="button" class="btn btn-success action-btn" (click)="submitNewAnswers(newRow)"><i class="fas fa-save"></i></button>
                    </td>
                </tr>
            </tbody>
        </table>
        <div class="button-container">
            <button class="btn btn-primary add-new" (click)="addNewRow()">Add New</button>
        </div>
    </div>
</div>
